import React, { useState, useEffect } from 'react';
import { useParams, useSearchParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  CircularProgress,
  Button,
  Divider,
  Chip,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Grid,
  IconButton,
} from '@mui/material';
import {
  Summarize as SummarizeIcon,
  Download as DownloadIcon,
  ArrowBack as ArrowBackIcon,
  Delete as DeleteIcon,
} from '@mui/icons-material';
import { getDocument, getDocumentSummary, deleteDocument } from '../services/api';

function DocumentViewer() {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const [document, setDocument] = useState(null);
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [summarizing, setSummarizing] = useState(false);
  const [summaryType, setSummaryType] = useState('general');
  const [error, setError] = useState(null);

  // Check if we should generate a summary immediately
  const shouldGenerateSummary = searchParams.get('summary') === 'true';

  useEffect(() => {
    const fetchDocument = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const documentData = await getDocument(id);
        setDocument(documentData);
        
        // Generate summary automatically if requested in URL
        if (shouldGenerateSummary) {
          handleSummarize();
        }
      } catch (error) {
        console.error('Error fetching document:', error);
        setError('Failed to load document. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchDocument();
  }, [id, shouldGenerateSummary]);

  const handleSummarize = async () => {
    setSummarizing(true);
    setError(null);
    
    try {
      const summaryResult = await getDocumentSummary(id, summaryType, 500);
      setSummary(summaryResult.summary);
    } catch (error) {
      console.error('Error generating summary:', error);
      setError('Failed to generate summary. Please try again.');
    } finally {
      setSummarizing(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this document?')) return;
    
    try {
      await deleteDocument(id);
      navigate('/search', { state: { message: 'Document deleted successfully' } });
    } catch (error) {
      console.error('Error deleting document:', error);
      setError('Failed to delete document. Please try again.');
    }
  };

  const handleDownload = () => {
    if (document && document.fileUrl) {
      window.open(document.fileUrl, '_blank');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error || !document) {
    return (
      <Container>
        <Typography variant="h6" color="error" sx={{ mt: 4 }}>
          {error || 'Document not found'}
        </Typography>
        <Button 
          startIcon={<ArrowBackIcon />}
          onClick={() => navigate('/search')}
          sx={{ mt: 2 }}
        >
          Back to Search
        </Button>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <IconButton 
            onClick={() => navigate('/search')}
            sx={{ mr: 2 }}
            color="primary"
          >
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h4" component="h1">
            {document.title}
          </Typography>
        </Box>
        <Box>
          <Chip 
            icon={<DownloadIcon />} 
            label="Download Original"
            onClick={handleDownload}
            sx={{ mr: 1 }}
            color="primary"
            variant="outlined"
          />
          <Chip 
            icon={<DeleteIcon />} 
            label="Delete"
            onClick={handleDelete}
            color="error"
            variant="outlined"
          />
        </Box>
      </Box>
      
      <Grid container spacing={4}>
        <Grid item xs={12} md={7}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
              <Typography variant="h6" gutterBottom>
                Document Content
              </Typography>
              <Chip 
                label={document.fileType?.toUpperCase() || 'DOCUMENT'} 
                size="small"
                color="primary"
              />
            </Box>
            <Divider sx={{ mb: 2 }} />
            
            {document.fileUrl ? (
              <Box sx={{ textAlign: 'center' }}>
                <Typography variant="body1" paragraph>
                  This is a preview of {document.fileName || 'the document'}.
                </Typography>
                <Button
                  variant="contained"
                  startIcon={<DownloadIcon />}
                  onClick={handleDownload}
                >
                  Download to View
                </Button>
              </Box>
            ) : (
              <Typography variant="body1" color="text.secondary">
                Document content not available for preview.
              </Typography>
            )}
          </Paper>
        </Grid>

        <Grid item xs={12} md={5}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Document Summary
            </Typography>
            
            <Box sx={{ mb: 3 }}>
              <FormControl fullWidth variant="outlined" size="small" sx={{ mb: 2 }}>
                <InputLabel id="summary-type-label">Summary Type</InputLabel>
                <Select
                  labelId="summary-type-label"
                  value={summaryType}
                  onChange={(e) => setSummaryType(e.target.value)}
                  label="Summary Type"
                  disabled={summarizing}
                >
                  <MenuItem value="general">General Summary</MenuItem>
                  <MenuItem value="key_points">Key Points</MenuItem>
                  <MenuItem value="detailed">Detailed Summary</MenuItem>
                </Select>
              </FormControl>
              
              <Button
                variant="contained"
                startIcon={summarizing ? <CircularProgress size={20} /> : <SummarizeIcon />}
                onClick={handleSummarize}
                disabled={summarizing}
                fullWidth
              >
                {summarizing ? 'Generating...' : 'Generate Summary'}
              </Button>
            </Box>

            {summary ? (
              <>
                <Divider sx={{ my: 2 }} />
                <Typography variant="body1" sx={{ whiteSpace: 'pre-line' }}>
                  {summary}
                </Typography>
              </>
            ) : (
              <Typography variant="body2" color="text.secondary">
                Click the button above to generate a summary of this document.
              </Typography>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default DocumentViewer;
